package com.organicapp.model;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Entity@Table(name = "tbl_items")@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Item {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    private String name;    private String  category;    private String   price;    private String  quantity;    private String   quantityUnit;    private String  discountPrice;    private String shopName;    private String  imageUrl;    private String details;    private String  additionalDetails;}